import tarfileimport osdef create_tar_GZ(targetfile, targetDir):    if '.tar.gz' not in targetfile:        targetfile += '.tar.gz'    with tarfile.open(targetfile,"w:gz") as tar:        tar.add(targetDir, arcname=os.path.basename(targetDir))    return 0def create_tar_bz2(targetfile,targetDir):    if '.tar.bz2' not in targetfile:        targetfile += '.tar'    with tarfile.open(targetfile,"w:bz2") as tar:        tar.add(targetDir, arcname=os.path.basename(targetDir))    return 0if __name__ == '__main__':    targetFilename = raw_input("Enter filename: ")    targetDir = raw_input("Enter Directory path: ")create_tar_GZ(targetFilename,targetDir)create_tar_bz2(targetFilename,targetDir)